package Conditional_Study;

public class Week_Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int Week=5;
		switch (Week) {
		case 1:System.out.println("This is ur first  day of week Mon");
		break;
		case 2:System.out.println("This is ur second day of week Tue");
		break;
		case 3:System.out.println("This is ur Third day of week Wed");
		break;
		case 4:System.out.println("This is urFifth day of week Thu");
		break;
		case 5:System.out.println("This is ur six day of week Fri");
		break;
		case 6:System.out.println("This is ur sventh day of week Sat");
		break;
		case 7:System.out.println("This is ur eight day of week Sun");
		break;
    	default:System.out.println("plz enter valid number 1 to 7");
			break;
		}

	}

}
