package ForLoop_Study;

public class DoWhileTable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i=6;
		int j=60;
		do
		{	
			System.out.println("******");

			System.out.println(i);
			i=i+6;
			System.out.println(j);
			j=j-6;
			
		}
		while(i<=60);
	
		while(j>=6);
	}

}

