package ForLoop_Study;

public class Loop_Study1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a;
		for(a=0; a<=10;a++)
		{
			System.out.println("The Value of  :"+a);
		}
		System.out.println("***************************");

	
	for(int i=10;i>=0;i--)
	{
		System.out.println("The value of i is :"+i);
	}

}}
