package ForLoop_Study;

public class while_Loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=10;
		while(a<=100) {
			System.out.println(a);
			
			a=a+10;
			
		}
	
	System.out.println("***********");	

	int j=100;
	while(j>=1)
	{
		System.out.println(j);
		j=j-10;
		
	}

}
}